import React from 'react'
import logo from '../images/logo.svg'
import { Link } from 'react-router-dom'
import axios from 'axios';
const Signup = () => {

    const [userinfo, setUserinfo] = useState({
        name: '',
        email: '',
        password: '',
        conf_pass: ''
    });

    const clearInput = () => {
        setUserinfo({
            name: '',
            email: '',
            password: '',
            conf_pass: ''
        });
    }

    const saveUser = (e) => {
        try {
            e.preventDefault();
            const headers = {
                'Content-Type': 'application/json'
            }
            axios.post('http://localhost:9000/users/createUser', userinfo, {
                headers: headers
            }).then(res => {
                // console.log('users are',res.data);  
                // console.log('users are',res.data[0].id);                
                const user_id = res.data[0].id;
                let users = [];
                const Token = res.data[0].token;
                localStorage.setItem("auth-token", Token);

                axios.get(`http://localhost:5000/users/fetchUser/${user_id}`,
                    { headers: { "x-auth-token": Token } })
                    .then(res => {
                        // console.log('users are',res);
                        // users = JSON.stringify(res.data);
                        users = res.data[0];
                        // console.log('bd',users);
                        registereduser = ({
                            token: users.token,
                            firstname: users.firstname,
                            lastname: users.lastname,
                            email: users.email,
                            role: users.role,
                            id: users._id

                        });
                        setRole(registereduser.role);
                        setUserRegistered(registereduser);
                    }).catch(err => {
                        console.log(err);
                        err.response.data.msg && setError(err.response.data.msg);

                    });

            }).catch(err => {
                console.log(err);
                err.response.data.msg && setError(err.response.data.msg);

            });

            clearInput();
        }
        catch (err) {
            err.response.data.msg && setError(err.response.data.msg);
        }
    }
    return (
        <div className='parent' >
            <div className='box22'>
                <div className='log'>
                    <img src={logo} style={{ 'width': '70px', 'height': '40px', 'marginLeft': '29px' }} />
                    <h2 className='ll' >Signup</h2>
                </div>
                <div className='kl'>
                    <div className='form'>
                        <input type='text' placeholder='Name' />
                        <input type='text' placeholder='Email' />
                        <input type='text' placeholder='Password' />
                        <input type='text' placeholder='Confirm Password' />
                        <button className='btnn'>Submit</button>
                    </div>
                    <Link className='para2' to='/login'>Already have an account yet? Login</Link>
                </div>
            </div>
        </div>
    )
}

export default Signup
