import React, { useState } from 'react'
import axios from 'axios';
import './Admin.css'
const ProductInsert = () => {


    const [products, setProducts] = useState({
        pname: '',
        price: '',
        description: '',
        category: '',
        stock: '',
        seller: '',
    })

    // const [images, setImages] = useState([]);
    // const [imagesPreview, setImagesPreview] = useState([]);

    const categories = [
        'amplifiers',
        'speakers',
        'televisions',
        'hometheatres',
        'spares'
    ];


    const saveProducts = (e) => {
        try {
            e.preventDefault();
            const headers = {
                'Content-Type': 'application/json'
            }
            axios.post('http://localhost:5000/api/product/insertproducts', products, {
                headers: headers
            }).then(res => {
                alert('signup successfully')
                if (res.data[0].success == 'true') {
                    alert("product insert successfully")
                    console.log(res.data[1]);
                }
            })
        }
        catch (err) {
        }
    }
    const updateField = e => {

        setProducts({
            ...products,
            [e.target.name]: e.target.value
        });
    };

    return (
        <div className='products'>
            <div>
                <form onSubmit={saveProducts} className='productForm'>
                    <input type='text' placeholder='Product Name' onChange={e => updateField(e.target.value)} value={products.pname} />
                    <input type='text' placeholder='Price' onChange={e => updateField(e.target.value)} value={products.price} />
                    <textarea placeholder='Enter product description' onChange={e => updateField(e.target.value)} value={products.descriptiondescription}></textarea>
                    <label htmlFor="category_field">Category</label>
                    <select onChange={e => updateField(e.target.value)} className="form-control" id="category_field">
                        <option value="">Select</option>
                        {categories.map(category => (
                            <option key={category} value={products.category}>{category}</option>
                        ))}
                    </select>
                    <input type='number' placeholder='Stocks' onChange={e => updateField(e.target.value)} value={products.stock} />
                    <input type='text' placeholder='Seller Name' onChange={e => updateField(e.target.value)} value={products.seller} />

                </form>
            </div>


        </div>
    )
}

export default ProductInsert
