import React, { useState, useEffect,use } from 'react'
import Navbar from './Components/Navbar/Navbar'
import AllRoutes from './AllRoute'
import Signup from './Auth/Signup'
import Login from './Auth/Login'
import { Routes, Route } from 'react-router-dom'
import userContext from './context/userContext';
import axios from 'axios'
const App = () => {
    let [registereduser, setRegisteredUser] = useState({
        token: undefined,
        name: undefined,
        email: undefined,
        role: undefined,
        id: undefined
    });
    const [userData, setUserData] = useState({
        token: undefined,
        user: undefined
    });
    let [userregistered, setUserRegistered] = useState('');
    let [role, setRole] = useState('');
    const [loggedInUser, setLoggedInUser] = useState('');
    const [usersdata, setUsersData] = useState([]);

    useEffect(() => {
        const checkLoggedIn = async () => {
            let token = localStorage.getItem("auth-token");
            console.log('auth-token', token);
            if (token === null) {
                localStorage.setItem("auth-token", "");
                token = "";
            }
            const tokenRes = await axios.post('http://localhost:5000/users/tokenIsValid',
                null,
                { headers: { "x-auth-token": token } }
            );
            if (tokenRes.data) {
                const userRes = await axios.get('http://localhost:5000/users/user',
                    { headers: { "x-auth-token": token } }
                );
                setUserData({
                    token,
                    user: userRes.data,
                })
                console.log('setuserdata', userData)
            }
        }
        checkLoggedIn();
    }, [])
    return (
        <>
            <userContext.Provider
                value={{
                    userData,
                    setUserData,
                    userregistered,
                    setUserRegistered,
                    role,
                    setRole,
                    registereduser,
                    setRegisteredUser,
                    loggedInUser,
                    setLoggedInUser,
                    usersdata,
                    setUsersData
                }}>
                <Navbar />
                <Routes>
                    <Route path='/' element={<AllRoutes />} />
                    <Route path='/login' element={<Login />} />
                    <Route path='/Signup' element={<Signup />} />
                </Routes>
            </userContext.Provider>

        </>
    )
}

export default App
