import React, { useState } from 'react'
import axios from 'axios';

const ProductInsert = () => {


    const [products, setProducts] = useState({
        pname: '',
        price: '',
        description: '',
        category: '',
        stock: '',
        seller: '',
    })

    const [images, setImages] = useState([]);
    const [imagesPreview, setImagesPreview] = useState([]);

    const categories = [
        'amplifiers',
        'speakers',
        'televisions',
        'hometheatres',
        'spares'
    ];

    const onImagesChange = (e) => {
        const files = Array.from(e.target.files);
        files.forEach(file => {
            const reader = new FileReader();
            reader.onload = () => {
                if (reader.readyState == 2) {
                    setImagesPreview(oldArray => [...oldArray, reader.result])
                    setImages(oldArray => [...oldArray, file])
                }
            }
            reader.readAsDataURL(file)
        })
    }

    const saveProducts = (e) => {
        try {
            e.preventDefault();
            const formData = new FormData();
            images.forEach (image => {
                formData.append('images', image)
            })
            const headers = {
                'Content-Type': 'application/json'
            }
            axios.post('http://localhost:5000/api/product/insertproducts', products, formData,{
                headers: headers
            }).then(res => {
                alert('signup successfully')
                if (res.data[0].success == 'true') {
                    alert("product insert successfully")
                    console.log(res.data[1]);
                }
            })
        }
        catch (err) {
        }
    }
    const updateField = e => {

        setProducts({
            ...products,
            [e.target.name]: e.target.value
        });
    };

    return (
        <div>
            <form onSubmit={saveProducts}>
                <input type='text' placeholder='Product Name' onChange={e => setProducts(e.target.value)} value={products.pname} />
                <input type='text' placeholder='Price' onChange={e => setProducts(e.target.value)} value={products.price} />
                <textarea placeholder='Enter product description' onChange={e => setProducts(e.target.value)} value={products.descriptiondescription}></textarea>
                <label htmlFor="category_field">Category</label>
                <select onChange={e => setProducts(e.target.value)} className="form-control" id="category_field">
                    <option value="">Select</option>
                    {categories.map(category => (
                        <option key={category} value={products.category}>{category}</option>
                    ))}
                </select>
                <input type='number' placeholder='Stocks' onChange={e => setProducts(e.target.value)} value={products.stock} />
                <input type='text' placeholder='Seller Name' onChange={e => setProducts(e.target.value)} value={products.seller} />
                <div className='custom-file'>
                    <input
                        type='file'
                        name='product_images'
                        className='custom-file-input'
                        id='customFile'
                        multiple
                        onChange={onImagesChange}

                    />

                    <label className='custom-file-label' htmlFor='customFile'>
                        Choose Images
                    </label>
                </div>
                {imagesPreview.map(image => (
                    <img
                        className="mt-3 mr-2"
                        key={image}
                        src={image}
                        alt={`Image Preview`}
                        width="55"
                        height="52"
                    />
                ))}



            </form>

        </div>
    )
}

export default ProductInsert
