import React, { useState, useContext } from 'react';
import './login.css'
import userContext from '../context/userContext';
import logo from '../images/logo.svg'
import { Link } from 'react-router-dom'
import axios from 'axios';
import Admin from '../Admin';
import Customer from '../Customer';
const Login = () => {

    const [userlogin, setUserlogin] = useState({
        email: '',
        password: ''
    });
    let { loggedInUser, setLoggedInUser} = useContext(userContext);
    const { role, setRole } = useContext(userContext);



   
        const clearInput = () => {
            setUserlogin({

                email: '',
                password: ''
              });
        }

    const loginUser = (e) => {
        try {
            e.preventDefault();
            axios.post('http://localhost:5000/users/login', userlogin)
                .then(res => {
                    // console.log('login user is',res.data[0]);
                    const loginUser = res.data[0];
                    setLoggedInUser({
                        token: loginUser.token,
                        name: loginUser.name,
                        email: loginUser.email,
                        role: loginUser.role,
                        id: loginUser.id

                    });
                    setRole(loginUser.role);

                    localStorage.setItem("auth-token", loginUser.token);


                    // console.log('logged in user',loggedInUser);
                }).catch(err => {
                    console.log(err);
                    err.response.data.msg && setError(err.response.data.msg);

                });
            clearInput();
        }
        catch (err) {
            err.response.data.msg && setError(err.response.data.msg);
        }
    }
    return (
        <div className='parent'>

            <div className='box2'>

                <div className='log'>
                    <img src={logo} style={{ 'width': '70px', 'height': '40px', 'marginLeft': '29px' }} />
                    <h2 className='ll' >Login</h2>
                </div>
                <div className='kl'>
                    <div className='form'>
                        <input type='text' placeholder='username@gmail.com' />
                        <input type='text' placeholder='Password' />
                        <button className='btnnn'>Sign In</button>
                    </div>
                    <p className='para'>Forget Password?</p>
                    <Link className='para2' to='/signup'>Don't have an account yet? Register</Link>
                </div>

            </div>
        </div>
    )
}

export default Login
